mapper.update:
update tbs_sale_rec set ord_no=#{ord_no},
sale_type=#{sale_type}, sale_user_num=#{sale_user_num}, cust_num=#{cust_num}, cust_nm=#{cust_nm}, addr=#{addr}, city=#{city}, state=#{state}, zip_code=#{zip_code}, tot_sale_amt=#{tot_sale_amt}, tot_tax_amt=#{tot_tax_amt}, shopping_cost=#{shopping_cost}, tot_ord_amt=#{tot_ord_amt},
sale_note=#{sale_note}, editor=#{editor}, crt_ymdt=#{crt_ymdt}, mod_ymdt=#{mod_ymdt} where sale_num = #{sale_num}

update tbs_sale_rec
set
tot_pay_amt=tot_pay_amt+#{tot_pay_amt},
last_pay_ymdt=#{last_pay_ymdt}
where
sale_num = #{sale_num}

mapper.insert:
insert into
tbs_sale_rec_del select DATE_FORMAT(now(), '%Y%m%d%H%i%s'), t1.* FROM
tbs_sale_rec t1 where sale_num = #{sale_num}
